import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom'
import {useParams} from 'react-router-dom'
import Navbar from './Navbar.js'
import client from './client.js'
import Footer from './Footer.js'
import {createClient} from 'contentful'


function Post() {
		const { id } = useParams()
		const [post, setPost] = useState(null)
			useEffect(() => {
				const getPost = async () => {											
					try {
					const entry = await client.getEntry(id)
						setPost(entry)
					}
					catch (error) {
						console.log(error)
					}	
				}
				getPost()
			}, [id])

			if (post === null) {
				return <div>Loading post...</div>;
			}
		return (
					<>
					<div className="my-[29%]"> </div>
					<h1 className="text-slate-400 text-9xl translate-y-[-430%] mx-[18%] min-[500px] max-[500px] truncate  "> {post.fields.title} </h1>
					<div className="translate-x-[10%]">
					<Navbar  />
					</div>
					<h1 className="relative text-white text-7xl translate-y-[-100%] translate-x-[120%]  w-fit"> {post.fields.date} </h1> 
			{post.fields.content.content.map((contentBlock, index) => {
				if (contentBlock.nodeType === 'paragraph') {
				 if (contentBlock.nodeType === 'text') {
					 return <p key={index}>{contentBlock.value}</p>;
				 } 
				}

				if (contentBlock.nodeType === 'embedded-asset-block') {
					        const imgUrl = contentBlock.data.target.fields.file.url;
					        return (
							          <div key={index}>
							            <img src={`https:${imgUrl}`} />
							          </div>
							        );
					      } else if (contentBlock.nodeType === 'hyperlink') {
						              const url = contentBlock.data.uri;
						              return (
								                <div key={index}>
								                  <a className="hover:underline" href={url} target="_blank" rel="noopener noreferrer">
								                    {contentBlock.content.map((text, textIndex) => (
											                    <span key={textIndex}>{text.value}</span>
											                  ))}
								                  </a>
								                </div>
								              );
								          } else {
										          return null; // Ignore other node types or handle them accordingly
										        }
			})}






					<div className="mt-[70%] translate-x-[3%]">
					<Footer />
					</div>

					</>
				)

}
export default Post
