import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom'
import {useParams} from 'react-router-dom'
import Navbar from './Navbar.js'
import client from './client.js'
import Footer from './Footer.js'
import {createClient} from 'contentful'


function Post() {
	const { id } = useParams()
	const [post, setPost] = useState(null)

	useEffect(() => {
	const getPost = async () => {
	try {
	const entry = await client.getEntry(id)
	console.log(entry)
	setPost(entry)
	}
	catch (error) {
	console.log(error)
	}
	}
		getPost()
	}, [id])
	if (post === null) {
		    return <div>Loading post...</div>;
		  } 


return (
	<>
	        <div className="my-[29%]"> </div>
	        <h1 className="text-slate-400 text-9xl translate-y-[-430%] translate-x-[20%] min-w-[500px] max-w-[500px] "> {post.fields.title} </h1>
	        <div className="translate-x-[10%]">
	        <Navbar  />
	</div>

	<h1 className="relative text-white text-7xl translate-y-[-100%] translate-x-[120%] w-fit">
	  {post.fields.date}
	</h1>
	{post.fields.content.content.map((contentBlock, index) => {
		  if (contentBlock.nodeType === 'embedded-asset-block') {
			      const imageUrl = `https:${contentBlock.data.target.fields.file.url}`;
			      return <img className="mx-[8%] my-[2%] relative max-w-[1200px] " src={imageUrl} alt="image" key={index} /> ;
			    } else if (contentBlock.nodeType === 'paragraph') {
				        return (
						      <div className="text-white max-w-7xl translate-x-[12%]" key={index}>
						        {contentBlock.content.map((node, nodeIndex) => {
								          if (node.nodeType === 'text') {
										              return <p key={nodeIndex}>{node.value}</p>;
										            } else if (node.nodeType === 'hyperlink') {
												                const url = node.data.uri;
												                return (
															              <a
															                className="hover:underline"
															                href={url}
															                target="_blank"
															                rel="noopener noreferrer"
															                key={nodeIndex}
															              >
															                {node.content.map((text, textIndex) => (
																		                  <span key={textIndex}>{text.value}</span>
																		                ))}
															              </a>
															            );
												              } else {
														                  return null;
														                }
								        })}
						      </div>
						    );
				      } else {
					          return null;
					        }
	})}
	<div className="mt-[70%] translate-x-[3%]">
	<Footer />
	</div>
	</>
);
}

export default Post
