import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom'
import {useParams} from 'react-router-dom'
import Navbar from './Navbar.js'
import client from './client.js'
import Footer from './Footer.js'
import {createClient} from 'contentful'


function Post() {
	const { id } = useParams()
	const [post, setPost] = useState(null)

	useEffect(() => {
	const getPost = async () => {
	try {
	const entry = await client.getEntry(id)
	console.log(entry)
	setPost(entry)
	}
	catch (error) {
	console.log(error)
	}
	}
		getPost()
	}, [id])

	if (post === null) {
		    return <div>Loading post...</div>;
		  }


return (
	<>
	        <div className="my-[29%]"> </div>
	        <h1 className="text-slate-400 text-9xl translate-y-[-430%] translate-x-[40%]"> {post.fields.title} </h1>
	        <Navbar />

			 <h1 className="relative text-white text-7xl translate-y-[-100%] translate-x-[140%] hover:text-slate-400 w-fit"> {post.fields.date} </h1> 
	{post.fields.content.content.map((contentBlock, index) => {
		      if (contentBlock.nodeType === 'paragraph') {
			              return (
					                <div className="text-white " key={index}>
					                  {contentBlock.content.map((paragraph, paragraphIndex) => (
								                <p className="translate-x-[20%] max-w-3xl" key={paragraphIndex}>{paragraph.value}</p>
								              ))}
					                </div>
					              );
			            } else {
					            return null;
					          }
		    })}
	        <div className="mt-[70%] translate-x-[3%]">
	        <Footer />
	        </div>
	        </>
)

}
export default Post
